contracts/Backroom.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Backroom is Ownable {
    using SafeERC20 for ERC20;

    address public protocolFeeDestination;
    uint256 public protocolFeePercent;
    uint256 public subjectFeePercent;

    address public token;

    uint256 public multiplier1;
    uint256 public multiplier2;
    uint256 public multiplier3;

    // SharesSubject => Curve Index (1, 2, or 3)
    mapping(address => uint256) public subjectCurve;

    event Trade(
        address trader,
        address subject,
        bool isBuy,
        uint256 shareAmount,
        uint256 tokenAmount,
        uint256 protocolTokenAmount,
        uint256 subjectTokenAmount,
        uint256 supply,
        uint256 multiplier
    );

    // SharesSubject => (Holder => Balance)
    mapping(address => mapping(address => uint256)) public sharesBalance;

    // SharesSubject => Supply
    mapping(address => uint256) public sharesSupply;

    constructor(
        address _feeDestination,
        uint256 _protocolFeePercent,
        uint256 _subjectFeePercent,
        address _token,
        uint256 _multiplier1,
        uint256 _multiplier2,
        uint256 _multiplier3
    ) Ownable(msg.sender) {
        require(_multiplier1 > 0, "Multiplier1 cannot be zero");
        require(_multiplier2 > 0, "Multiplier2 cannot be zero");
        require(_multiplier3 > 0, "Multiplier3 cannot be zero");

        protocolFeeDestination = _feeDestination;
        protocolFeePercent = _protocolFeePercent;
        subjectFeePercent = _subjectFeePercent;
        token = _token;
        multiplier1 = _multiplier1;
        multiplier2 = _multiplier2;
        multiplier3 = _multiplier3;
    }

    function setFeeDestination(address _feeDestination) public onlyOwner {
        protocolFeeDestination = _feeDestination;
    }

    function setProtocolFeePercent(uint256 _feePercent) public onlyOwner {
        protocolFeePercent = _feePercent;
    }

    function setSubjectFeePercent(uint256 _feePercent) public onlyOwner {
        subjectFeePercent = _feePercent;
    }

    function getPrice(
        uint256 supply,
        uint256 amount,
        address sharesSubject
    ) public view returns (uint256) {
        uint256 sum1 = supply == 0
            ? 0
            : ((supply - 1) * (supply) * (2 * (supply - 1) + 1)) / 6;
        uint256 sum2 = supply == 0 && amount == 1
            ? 0
            : ((supply - 1 + amount) *
                (supply + amount) *
                (2 * (supply - 1 + amount) + 1)) / 6;
        uint256 summation = sum2 - sum1;

        uint256 multiplier;
        uint256 curveIndex = subjectCurve[sharesSubject];
        if (curveIndex == 1) {
            multiplier = multiplier1;
        } else if (curveIndex == 2) {
            multiplier = multiplier2;
        } else if (curveIndex == 3) {
            multiplier = multiplier3;
        } else {
            multiplier = multiplier1; // Default to first curve
        }

        return (summation * 1 ether) * multiplier;
    }

    function getBuyPrice(
        address sharesSubject,
        uint256 amount
    ) public view returns (uint256) {
        return getPrice(sharesSupply[sharesSubject], amount, sharesSubject);
    }

    function getSellPrice(
        address sharesSubject,
        uint256 amount
    ) public view returns (uint256) {
        return
            getPrice(
                sharesSupply[sharesSubject] - amount,
                amount,
                sharesSubject
            );
    }

    function getBuyPriceAfterFee(
        address sharesSubject,
        uint256 amount
    ) public view returns (uint256) {
        uint256 price = getBuyPrice(sharesSubject, amount);
        uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
        uint256 subjectFee = (price * subjectFeePercent) / 1 ether;
        return price + protocolFee + subjectFee;
    }

    function getSellPriceAfterFee(
        address sharesSubject,
        uint256 amount
    ) public view returns (uint256) {
        uint256 price = getSellPrice(sharesSubject, amount);
        uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
        uint256 subjectFee = (price * subjectFeePercent) / 1 ether;
        return price - protocolFee - subjectFee;
    }

    function buyShares(
        address sharesSubject,
        uint256 amount,
        uint256 curveIndex
    ) public {
        uint256 supply = sharesSupply[sharesSubject];
        require(
            supply > 0 || sharesSubject == msg.sender,
            "Only the shares' subject can buy the first share"
        );

        // Set curve for subject on first share purchase
        if (supply == 0 && sharesSubject == msg.sender) {
            require(curveIndex >= 1 && curveIndex <= 3, "Invalid curve index");
            subjectCurve[sharesSubject] = curveIndex;
        }

        uint256 price = getPrice(supply, amount, sharesSubject);
        uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
        uint256 subjectFee = (price * subjectFeePercent) / 1 ether;

        sharesBalance[sharesSubject][msg.sender] =
            sharesBalance[sharesSubject][msg.sender] +
            amount;
        sharesSupply[sharesSubject] = supply + amount;

        uint256 multiplier;
        curveIndex = subjectCurve[sharesSubject];

        if (curveIndex == 1) {
            multiplier = multiplier1;
        } else if (curveIndex == 2) {
            multiplier = multiplier2;
        } else if (curveIndex == 3) {
            multiplier = multiplier3;
        } else {
            multiplier = multiplier1; // Default to first curve
        }

        emit Trade(
            msg.sender,
            sharesSubject,
            true,
            amount,
            price,
            protocolFee,
            subjectFee,
            supply + amount,
            multiplier
        );

        ERC20(token).safeTransferFrom(msg.sender, address(this), price);
        ERC20(token).safeTransferFrom(
            msg.sender,
            protocolFeeDestination,
            protocolFee
        );
        ERC20(token).safeTransferFrom(msg.sender, sharesSubject, subjectFee);
    }

    function sellShares(address sharesSubject, uint256 amount) public {
        uint256 supply = sharesSupply[sharesSubject];
        require(supply > amount, "Cannot sell the last share");

        uint256 price = getPrice(supply - amount, amount, sharesSubject);
        uint256 protocolFee = (price * protocolFeePercent) / 1 ether;
        uint256 subjectFee = (price * subjectFeePercent) / 1 ether;

        require(
            sharesBalance[sharesSubject][msg.sender] >= amount,
            "Insufficient shares"
        );

        sharesBalance[sharesSubject][msg.sender] =
            sharesBalance[sharesSubject][msg.sender] -
            amount;
        sharesSupply[sharesSubject] = supply - amount;

        uint256 multiplier;
        uint256 curveIndex = subjectCurve[sharesSubject];

        if (curveIndex == 1) {
            multiplier = multiplier1;
        } else if (curveIndex == 2) {
            multiplier = multiplier2;
        } else if (curveIndex == 3) {
            multiplier = multiplier3;
        } else {
            multiplier = multiplier1; // Default to first curve
        }

        emit Trade(
            msg.sender,
            sharesSubject,
            false,
            amount,
            price,
            protocolFee,
            subjectFee,
            supply - amount,
            multiplier
        );

        ERC20(token).safeTransfer(msg.sender, price - protocolFee - subjectFee);
        ERC20(token).safeTransfer(protocolFeeDestination, protocolFee);
        ERC20(token).safeTransfer(sharesSubject, subjectFee);
    }
}

contracts/MerkleTreeDistributor.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract MerkleTreeDistributor is Ownable {
    using SafeERC20 for ERC20;

    ERC20 public immutable token;
    bytes32 public merkleRoot;

    mapping(address => bool) public claimed;

    event Claimed(address indexed account, uint256 amount);
    event RootChanged(bytes32 indexed oldRoot, bytes32 indexed newRoot);

    constructor(address _token, bytes32 _merkleRoot) Ownable(msg.sender) {
        token = ERC20(_token);
        merkleRoot = _merkleRoot;
    }

    function claim(
        address account,
        uint256 amount,
        bytes32[] calldata merkleProof
    ) external {
        require(!claimed[account], "Already claimed");

        bytes32 leaf = keccak256(abi.encodePacked(account, amount));
        require(
            MerkleProof.verify(merkleProof, merkleRoot, leaf),
            "Invalid proof"
        );

        claimed[account] = true;
        token.safeTransfer(account, amount);

        emit Claimed(account, amount);
    }

    function isClaimed(address account) external view returns (bool) {
        return claimed[account];
    }

    function changeRoot(bytes32 _newMerkleRoot) external onlyOwner {
        bytes32 oldRoot = merkleRoot;
        merkleRoot = _newMerkleRoot;

        emit RootChanged(oldRoot, _newMerkleRoot);
    }

    function withdrawRemainingTokens() external onlyOwner {
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(owner(), balance);
    }
}

contracts/Presale.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title Presale
 * @dev Presale contract for Backroom token with USDC contributions
 */
contract Presale is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public immutable usdcToken;

    uint256 public softCap;
    uint256 public hardCap;
    uint256 public minContribution;
    uint256 public maxContribution;

    uint256 public saleStartTime;
    uint256 public saleEndTime;
    uint256 public totalRaised;
    bool public saleFinalized; // Has sale been finalized
    bool public saleSuccessful; // Did sale meet soft cap

    uint256 public constant SALE_DURATION = 24 hours;

    mapping(address => uint256) public contributions; // USDC contributed per address
    mapping(address => bool) public whitelist; // Whitelisted addresses

    event SaleStarted(uint256 startTime, uint256 endTime);
    event ContributionMade(address indexed contributor, uint256 amount);
    event SaleFinalized(bool successful, uint256 totalRaised);
    event RefundClaimed(address indexed contributor, uint256 amount);
    event FundsWithdrawn(uint256 amount);
    event UserWhitelisted(address indexed user);

    modifier onlyDuringSale() {
        require(saleStartTime > 0, "Sale not started");
        require(block.timestamp >= saleStartTime, "Sale not started");
        require(block.timestamp <= saleEndTime, "Sale ended");
        require(totalRaised < hardCap, "Hard cap reached");
        _;
    }

    modifier onlyAfterSale() {
        require(saleFinalized, "Sale not finalized");
        _;
    }

    constructor(
        address _usdcToken,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _minContribution,
        uint256 _maxContribution
    ) Ownable(msg.sender) {
        require(_usdcToken != address(0), "USDC token address cannot be zero");
        require(_softCap > 0, "Soft cap must be > 0");
        require(_hardCap > _softCap, "Hard cap must be > soft cap");
        require(_minContribution > 0, "Min contribution must be > 0");
        require(
            _maxContribution >= _minContribution,
            "Max must be >= min contribution"
        );
        require(
            _maxContribution <= _hardCap,
            "Max contribution cannot exceed hard cap"
        );

        usdcToken = IERC20(_usdcToken);
        softCap = _softCap;
        hardCap = _hardCap;
        minContribution = _minContribution;
        maxContribution = _maxContribution;
    }

    /**
     * @dev Add multiple users to whitelist (admin only)
     */
    function addMultipleToWhitelist(
        address[] calldata _users
    ) external onlyOwner {
        for (uint256 i = 0; i < _users.length; i++) {
            require(_users[i] != address(0), "Invalid address");
            whitelist[_users[i]] = true;
            emit UserWhitelisted(_users[i]);
        }
    }

    /**
     * @dev Start the presale (admin only)
     */
    function startSale() external onlyOwner {
        require(
            block.timestamp > saleEndTime || saleStartTime == 0,
            "Sale already active or finalized"
        );
        require(!saleFinalized, "Sale already finalized");

        saleStartTime = block.timestamp;
        saleEndTime = block.timestamp + SALE_DURATION;

        emit SaleStarted(saleStartTime, saleEndTime);
    }

    /**
     * @dev Contribute USDC to the presale
     */
    function deposit(uint256 _amount) external onlyDuringSale nonReentrant {
        require(whitelist[msg.sender], "Address not whitelisted");
        require(_amount >= minContribution, "Below minimum contribution");
        require(contributions[msg.sender] == 0, "Already contributed");
        require(_amount <= maxContribution, "Exceeds maximum contribution");
        require(totalRaised + _amount <= hardCap, "Would exceed hard cap");

        // Transfer USDC from user to this contract
        usdcToken.safeTransferFrom(msg.sender, address(this), _amount);

        contributions[msg.sender] = _amount;
        totalRaised += _amount;

        emit ContributionMade(msg.sender, _amount);

        if (totalRaised >= hardCap) {
            _finalizeSale();
        }
    }

    /**
     * @dev Finalize the sale after 24 hours or when hard cap is reached
     */
    function finalizeSale() external {
        require(!saleFinalized, "Sale already finalized");
        require(
            block.timestamp > saleEndTime || totalRaised >= hardCap,
            "Sale period not ended and hard cap not reached"
        );

        _finalizeSale();
    }

    /**
     * @dev Internal function to finalize sale
     */
    function _finalizeSale() internal {
        saleFinalized = true;
        saleSuccessful = totalRaised >= softCap;

        emit SaleFinalized(saleSuccessful, totalRaised);
    }

    /**
     * @dev Claim refund if sale failed (contributors only)
     */
    function claimRefund() external onlyAfterSale nonReentrant {
        require(!saleSuccessful, "Sale was successful, no refunds");

        uint256 refundAmount = contributions[msg.sender];
        require(refundAmount > 0, "No contribution found");

        contributions[msg.sender] = 0;

        usdcToken.safeTransfer(msg.sender, refundAmount);

        emit RefundClaimed(msg.sender, refundAmount);
    }

    /**
     * @dev Withdraw raised funds if sale was successful (admin only)
     */
    function withdrawFunds() external onlyOwner onlyAfterSale {
        require(saleSuccessful, "Sale was not successful");

        uint256 amount = usdcToken.balanceOf(address(this));
        require(amount > 0, "No funds to withdraw");

        usdcToken.safeTransfer(owner(), amount);

        emit FundsWithdrawn(amount);
    }
    // ************************************
    // ********** View Functions **********
    // ************************************

    /**
     * @dev Get sale status information
     */
    function getSaleInfo()
        external
        view
        returns (
            bool _saleFinalized,
            bool _saleSuccessful,
            uint256 _totalRaised,
            uint256 _startTime,
            uint256 _endTime
        )
    {
        return (
            saleFinalized,
            saleSuccessful,
            totalRaised,
            saleStartTime,
            saleEndTime
        );
    }

    /**
     * @dev Get contribution info for an address
     */
    function getContributionInfo(
        address _contributor
    ) external view returns (uint256 _contribution, bool _isWhitelisted) {
        return (contributions[_contributor], whitelist[_contributor]);
    }

    /**
     * @dev Get time remaining in sale
     */
    function getTimeRemaining() external view returns (uint256) {
        if (saleStartTime == 0 || block.timestamp >= saleEndTime) {
            return 0;
        }
        return saleEndTime - block.timestamp;
    }

    /**
     * @dev Calculate token allocation for a contributor (view only, for off-chain reference)
     * @param _contributor Address to calculate for
     * @param _totalTokensAllocated Total tokens allocated to presale (e.g., 5% of supply)
     */
    function calculateTokenAllocation(
        address _contributor,
        uint256 _totalTokensAllocated
    ) external view returns (uint256) {
        require(
            saleFinalized && saleSuccessful,
            "Sale not successfully finalized"
        );
        require(totalRaised > 0, "No contributions");

        uint256 contributorUSDC = contributions[_contributor];
        if (contributorUSDC == 0) return 0;

        // UserTokens = (User USDC / Total USDC Raised) × Total Allocated Tokens
        return (contributorUSDC * _totalTokensAllocated) / totalRaised;
    }
}

