// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title Presale
 * @dev Presale contract for Backroom token with USDC contributions
 */
contract Presale is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public immutable usdcToken;

    uint256 public saleStartTime;
    uint256 public saleEndTime;
    uint256 public totalRaised;
    bool public saleFinalized; // Has sale been finalized
    bool public saleSuccessful; // Was sale successful

    uint256 public constant SALE_DURATION = 24 hours;

    mapping(address => uint256) public contributions; // USDC contributed per address
    mapping(address => uint256) public whitelist; // Whitelisted addresses

    event SaleStarted(uint256 startTime, uint256 endTime);
    event ContributionMade(address indexed contributor, uint256 amount);
    event SaleFinalized(bool successful, uint256 totalRaised);
    event FundsWithdrawn(uint256 amount);
    event UserWhitelisted(address indexed user, uint256 tier);

    modifier onlyDuringSale() {
        require(saleStartTime > 0, "Sale not started");
        require(block.timestamp >= saleStartTime, "Sale not started");
        require(block.timestamp <= saleEndTime, "Sale ended");
        _;
    }

    modifier onlyAfterSale() {
        require(saleFinalized, "Sale not finalized");
        _;
    }

    constructor(address _usdcToken) Ownable(msg.sender) {
        require(_usdcToken != address(0), "USDC token address cannot be zero");

        usdcToken = IERC20(_usdcToken);
    }

    /**
     * @dev Add multiple users to whitelist (admin only)
     */
    function addMultipleToWhitelist(
        address[] calldata _users,
        uint256[] calldata _tiers
    ) external onlyOwner {
        require(
            _users.length == _tiers.length,
            "Users and tiers must have the same length"
        );
        for (uint256 i = 0; i < _users.length; i++) {
            require(_users[i] != address(0), "Invalid address");

            whitelist[_users[i]] = _tiers[i];
            emit UserWhitelisted(_users[i], _tiers[i]);
        }
    }

    /**
     * @dev Start the presale (admin only)
     */
    function startSale() external onlyOwner {
        require(
            block.timestamp > saleEndTime || saleStartTime == 0,
            "Sale already active or finalized"
        );
        require(!saleFinalized, "Sale already finalized");

        saleStartTime = block.timestamp;
        saleEndTime = block.timestamp + SALE_DURATION;

        emit SaleStarted(saleStartTime, saleEndTime);
    }

    /**
     * @dev Contribute USDC to the presale
     */
    function deposit(uint256 _amount) external onlyDuringSale nonReentrant {
        require(whitelist[msg.sender] > 0, "Address not whitelisted");
        require(
            contributions[msg.sender] <= whitelist[msg.sender],
            "Already contributed full amount"
        );
        require(
            _amount <= whitelist[msg.sender],
            "Amount must be less than or equal to whitelist"
        );

        // Transfer USDC from user to this contract
        usdcToken.safeTransferFrom(msg.sender, address(this), _amount);

        contributions[msg.sender] += _amount;
        totalRaised += _amount;

        emit ContributionMade(msg.sender, _amount);
    }

    /**
     * @dev Finalize the sale after 24 hours
     */
    function finalizeSale() external {
        require(!saleFinalized, "Sale already finalized");
        require(
            block.timestamp > saleEndTime,
            "Sale period not ended and hard cap not reached"
        );

        saleFinalized = true;
        saleSuccessful = true;

        emit SaleFinalized(saleSuccessful, totalRaised);
    }

    /**
     * @dev Withdraw raised funds if sale was successful (admin only)
     */
    function withdrawFunds() external onlyOwner onlyAfterSale {
        require(saleSuccessful, "Sale was not successful");

        uint256 amount = usdcToken.balanceOf(address(this));
        require(amount > 0, "No funds to withdraw");

        usdcToken.safeTransfer(owner(), amount);

        emit FundsWithdrawn(amount);
    }
    // ************************************
    // ********** View Functions **********
    // ************************************

    /**
     * @dev Get sale status information
     */
    function getSaleInfo()
        external
        view
        returns (
            bool _saleFinalized,
            bool _saleSuccessful,
            uint256 _totalRaised,
            uint256 _startTime,
            uint256 _endTime
        )
    {
        return (
            saleFinalized,
            saleSuccessful,
            totalRaised,
            saleStartTime,
            saleEndTime
        );
    }

    /**
     * @dev Get contribution info for an address
     */
    function getContributionInfo(
        address _contributor
    ) external view returns (uint256 _contribution, bool _isWhitelisted) {
        return (contributions[_contributor], whitelist[_contributor] > 0);
    }

    /**
     * @dev Get time remaining in sale
     */
    function getTimeRemaining() external view returns (uint256) {
        if (saleStartTime == 0 || block.timestamp >= saleEndTime) {
            return 0;
        }
        return saleEndTime - block.timestamp;
    }

    /**
     * @dev Calculate token allocation for a contributor (view only, for off-chain reference)
     * @param _contributor Address to calculate for
     * @param _totalTokensAllocated Total tokens allocated to presale (e.g., 5% of supply)
     */
    function calculateTokenAllocation(
        address _contributor,
        uint256 _totalTokensAllocated
    ) external view returns (uint256) {
        require(
            saleFinalized && saleSuccessful,
            "Sale not successfully finalized"
        );
        require(totalRaised > 0, "No contributions");

        uint256 contributorUSDC = contributions[_contributor];
        if (contributorUSDC == 0) return 0;

        // UserTokens = (User USDC / Total USDC Raised) Ã— Total Allocated Tokens
        return (contributorUSDC * _totalTokensAllocated) / totalRaised;
    }
}

contracts/Staking.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {IStaking} from "./interfaces/IStaking.sol";

/**
 * @title Staking
 * @dev Staking contract for $ROOM tokens with 2-week lock period
 * Features:
 * - Unlimited number of positions per user
 * - Each stake creates a unique position with positionId
 * - 2-week lock period for unstaked tokens
 * - Restake function to return pending tokens to active staking
 */
contract Staking is ReentrancyGuard, IStaking {
    using SafeERC20 for IERC20;

    IERC20 public immutable roomToken;

    uint256 public constant COOLDOWN_PERIOD = 1 minutes; // 2 weeks in seconds
    uint256 public nextPositionId = 1;

    mapping(uint256 => Position) public positions;
    mapping(address => uint256[]) public userPositions;
    mapping(address => uint256) public totalStaked;

    address[] public stakers;
    mapping(address => bool) public isRegistered;

    modifier onlyPositionOwner(uint256 positionId) {
        require(
            positions[positionId].owner == msg.sender,
            "Not position owner"
        );
        _;
    }

    modifier positionExists(uint256 positionId) {
        require(
            positions[positionId].owner != address(0),
            "Position does not exist"
        );
        _;
    }

    constructor(address _roomToken) {
        require(_roomToken != address(0), "ROOM token address cannot be zero");
        roomToken = IERC20(_roomToken);
    }

    /**
     * @dev Stake tokens and create a new position
     * @param amount Amount of tokens to stake
     * @return positionId The ID of the created position
     */
    function stake(uint256 amount) external nonReentrant returns (uint256) {
        require(amount > 0, "Amount must be greater than zero");
        roomToken.safeTransferFrom(msg.sender, address(this), amount);

        uint256 positionId = nextPositionId++;
        positions[positionId] = Position({
            positionId: positionId,
            owner: msg.sender,
            amount: amount,
            unlockTime: 0,
            stakeTime: block.timestamp,
            status: PositionStatus.Active
        });

        userPositions[msg.sender].push(positionId);
        totalStaked[msg.sender] += amount;

        if (!isRegistered[msg.sender]) {
            isRegistered[msg.sender] = true;
            stakers.push(msg.sender);
        }

        emit PositionCreated(positionId, msg.sender, amount, block.timestamp);

        return positionId;
    }

    /**
     * @dev Unstake tokens from a position (starts lock period)
     * @param positionId ID of the position to unstake
     */
    function unstake(
        uint256 positionId
    )
        external
        positionExists(positionId)
        onlyPositionOwner(positionId)
        nonReentrant
    {
        Position storage position = positions[positionId];
        require(
            position.status == PositionStatus.Active,
            "Position not active"
        );

        position.status = PositionStatus.Pending;
        position.unlockTime = block.timestamp + COOLDOWN_PERIOD;

        totalStaked[msg.sender] -= position.amount;

        emit PositionUnstaked(
            positionId,
            msg.sender,
            position.amount,
            position.unlockTime
        );
    }

    /**
     * @dev Claim tokens after lock period has ended
     * @param positionId ID of the position to claim
     */
    function claim(
        uint256 positionId
    )
        external
        positionExists(positionId)
        onlyPositionOwner(positionId)
        nonReentrant
    {
        Position storage position = positions[positionId];
        require(
            position.status == PositionStatus.Pending,
            "Position not pending"
        );
        require(
            block.timestamp >= position.unlockTime,
            "Lock period not ended"
        );

        uint256 amount = position.amount;
        position.status = PositionStatus.Claimed;

        roomToken.safeTransfer(msg.sender, amount);

        emit PositionClaimed(positionId, msg.sender, amount);
    }

    /**
     * @dev Restake tokens from a pending position (cancels lock period)
     * @param positionId ID of the position to restake
     */
    function restake(
        uint256 positionId
    )
        external
        positionExists(positionId)
        onlyPositionOwner(positionId)
        nonReentrant
    {
        Position storage position = positions[positionId];
        require(
            position.status == PositionStatus.Pending,
            "Position not pending"
        );

        position.status = PositionStatus.Active;
        position.unlockTime = 0;

        totalStaked[msg.sender] += position.amount;

        emit PositionRestaked(positionId, msg.sender, position.amount);
    }

    function getAllStakersWithPagination(
        uint256 offset,
        uint256 limit
    ) external view returns (address[] memory) {
        uint256 stakersLength = stakers.length;

        address[] memory result = new address[](limit);
        if (offset >= stakersLength) {
            result = new address[](limit);
            return result;
        }

        uint256 actualLimit = limit;
        if (offset + limit > stakersLength) {
            actualLimit = stakersLength - offset;
        }

        result = new address[](actualLimit);
        for (uint256 i = 0; i < actualLimit; i++) {
            result[i] = stakers[offset + i];
        }

        return result;
    }

    function getAllPositionsByStatusWithPagination(
        PositionStatus status,
        uint256 offset,
        uint256 limit
    ) external view returns (Position[] memory) {
        // First, count how many positions match the status
        uint256 matchCount = 0;
        for (uint256 i = 1; i < nextPositionId; i++) {
            if (positions[i].status == status) {
                matchCount++;
            }
        }

        Position[] memory result = new Position[](limit);

        if (offset >= matchCount) {
            return result;
        }

        uint256 actualLimit = limit;
        if (offset + limit > matchCount) {
            actualLimit = matchCount - offset;
        }

        uint256 resultIndex = 0;
        uint256 currentOffset = 0;

        for (
            uint256 i = 1;
            i < nextPositionId && resultIndex < actualLimit;
            i++
        ) {
            if (positions[i].status == status) {
                if (currentOffset >= offset) {
                    result[resultIndex] = positions[i];
                    resultIndex++;
                }
                currentOffset++;
            }
        }

        return result;
    }

    function getAllPositionsWithPagination(
        uint256 offset,
        uint256 limit
    ) external view returns (Position[] memory) {
        uint256 totalPositions = nextPositionId - 1; // positions start from ID 1

        Position[] memory result = new Position[](limit);

        if (offset >= totalPositions) {
            return result;
        }

        uint256 actualLimit = limit;
        if (offset + limit > totalPositions) {
            actualLimit = totalPositions - offset;
        }

        for (uint256 i = 0; i < actualLimit; i++) {
            result[i] = positions[offset + 1 + i]; // positions start from ID 1
        }

        return result;
    }

    function getUserPositionsPaginated(
        address user,
        uint256 offset,
        uint256 limit
    ) external view returns (uint256[] memory) {
        uint256[] storage allPositions = userPositions[user];
        uint256 length = allPositions.length;

        if (offset >= length) {
            return new uint256[](0);
        }

        uint256 end = offset + limit;
        if (end > length) {
            end = length;
        }

        uint256[] memory result = new uint256[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = allPositions[i];
        }

        return result;
    }

    function getUserPositions(
        address user
    ) external view returns (uint256[] memory) {
        return userPositions[user];
    }

    function getUserPositionCount(
        address user
    ) external view returns (uint256) {
        return userPositions[user].length;
    }

    /**
     * @dev Get position details
     * @param positionId ID of the position
     * @return Position struct
     */
    function getPosition(
        uint256 positionId
    ) external view returns (Position memory) {
        return positions[positionId];
    }

    /**
     * @dev Get user's total staked amount
     * @param user Address of the user
     * @return Total staked amount
     */
    function getUserTotalStaked(address user) external view returns (uint256) {
        return totalStaked[user];
    }

    /**
     * @dev Get time remaining until position can be claimed
     * @param positionId ID of the position
     * @return Time remaining in seconds (0 if not pending or already unlockable)
     */
    function getTimeUntilUnlock(
        uint256 positionId
    ) external view returns (uint256) {
        Position memory position = positions[positionId];
        if (position.status != PositionStatus.Pending) {
            return 0;
        }
        if (block.timestamp >= position.unlockTime) {
            return 0;
        }
        return position.unlockTime - block.timestamp;
    }

    /**
     * @dev Check if position can be claimed
     * @param positionId ID of the position
     * @return True if position can be claimed
     */
    function canClaim(uint256 positionId) external view returns (bool) {
        Position memory position = positions[positionId];
        return
            position.status == PositionStatus.Pending &&
            block.timestamp >= position.unlockTime;
    }
}
